func TestNewEthTx(t *testing.T) {
	// bs0, _ := hex.DecodeString("")
	// t.Logf("%s", string(bs0))
	// bs, _ := hex.DecodeString(string(bs0[2:]))
	bs, _ := hex.DecodeString("")
	t.Logf("%x", bs)
	tx := new(ETHTransaction)
	if err := tx.UnmarshalBinary(bs); err != nil {
		t.Fatal(err)
	}
	t.Logf("%+v", tx.inner)
	sig, pub, err := ETHSigner.RecoverSigAndPub(tx)
	if err != nil {
		t.Fatal(err)
	}
	t.Logf("pub:%x\nsig:%x", pub, sig)
}

func TestTkmTx(t *testing.T) {
	bridgeExtra := &BridgeReqExtra{
		Type:    0xff,
		From:    1,
		Height:  876343,
		Account: randomAddress(),
		Nonce:   9,
	}
	extra, err := json.Marshal(bridgeExtra)
	if err != nil {
		t.Fatal(err)
	}
	tx := &TkmTx{
		ChainID:    big.NewInt(70005),
		Nonce:      222,
		To:         nil,
		UseLocal:   true,
		Value:      nil,
		Data:       common.RandomBytes(68),
		Gas:        400000,
		GasPrice:   nil,
		GasTipCap:  nil,
		GasFeeCap:  nil,
		AccessList: nil,
		Extra:      extra,
		Version:    TxVersion,
		MultiSigs:  common.RandomByteSlices(5, 65),
		V:          big.NewInt(299),
		R:          nil,
		S:          nil,
	}
	ethtx := NewEthTx(tx)

	buf, err := ethtx.MarshalBinary()
	if err != nil {
		t.Fatal(err)
	}

	newtx := new(ETHTransaction)
	err = newtx.UnmarshalBinary(buf)
	if err != nil {
		t.Fatal(err)
	}

	newinner, ok := newtx.inner.(*TkmTx)
	if !ok {
		t.Fatalf("not a TkmTx, but: %s", reflect.TypeOf(newtx.inner).Name())
	}
	if newinner.like(tx) == false {
		t.Fatalf("not equal after endec: %+v -> %+v", tx, newinner)
	}
	t.Logf("before: %+v\nafter: %+v", tx, newinner)
}
